mapper.select:
SELECT s.grade, c.semester, c.course_code, c.course_id, c.title, c.credit, e.rank, c.type FROM enrollment e JOIN course c ON e.course_id = c.course_id JOIN student s ON e.user_id = s.user_id WHERE e.user_id = #{userId}
AND c.course_id = #{courseId}
AND c.semester = #{semester}

SELECT s.grade AS grade, c.semester AS semester, SUM(CASE WHEN c.type = '전공필수' THEN c.credit ELSE 0 END) AS majorRequired, SUM(CASE WHEN c.type = '전공선택' THEN c.credit ELSE 0 END) AS majorElective, SUM(CASE WHEN c.type = '교양' THEN c.credit ELSE 0 END) AS generalEducation FROM enrollment e JOIN course c ON e.course_id = c.course_id JOIN student s ON e.user_id = s.user_id WHERE e.user_id = #{userId}
AND c.course_id = #{courseId}
AND c.semester = #{semester}
GROUP BY s.grade, c.semester ORDER BY s.grade, c.semester

SELECT s.grade AS grade, c.semester AS semester, SUM(c.credit) AS requestedCredits, SUM(CASE WHEN e.score >= 60 THEN c.credit ELSE 0 END) AS acquiredCredits, AVG(e.score) AS avgScore, AVG(e.score / 20.0) AS avgGradePoint, MIN(e.rank) AS grading, COUNT(*) AS totalStudents FROM enrollment e JOIN course c ON e.course_id = c.course_id JOIN student s ON e.user_id = s.user_id WHERE e.user_id = #{userId}
AND c.course_id = #{courseId}
AND c.semester = #{semester}
GROUP BY s.grade, c.semester ORDER BY s.grade, c.semester

SELECT se.year, se.semester, c.type, s.grade, u.user_name AS professorName, c.course_code, c.title, c.credit, s.rank
FROM enrollment e JOIN course c ON e.course_id = c.course_id JOIN score s ON e.enrollment_id = s.enrollment_id JOIN semester se ON se.semester_id = c.semester_id JOIN user u ON u.user_id = c.user_id
WHERE e.user_id = #{userId} AND se.semester_id != #{req.semesterId} AND e.status = '수강완료'
AND s.grade = #{req.grade}
AND se.semester = #{req.semester}

SELECT c.type, u.user_name AS professorName, c.course_code, c.title, c.credit, s.rank, s.mid_score, s.fin_score, s.attendance_score, s.other_score, e.ev_score
FROM enrollment e JOIN course c ON e.course_id = c.course_id JOIN score s ON e.enrollment_id = s.enrollment_id JOIN user u ON u.user_id = c.user_id
WHERE e.user_id = #{userId} AND c.semester_id = #{semesterId}
