mapper.select:
SELECT course_code, course_id, classroom, type, semester, time, title, credit, max_std, d.dept_name, status, grade
FROM course c
join professor p
  on c.user_id = p.user_id
join department d
  on p.dept_id = d.dept_id
WHERE c.user_id = #{userId}

    AND year = #{year}


    AND semester = #{semester}

ORDER BY year DESC, title ASC

SELECT review, average
FROM survey
WHERE course_id = #{courseId}

mapper.update:
UPDATE course
SET classroom = #{classroom}
,type = #{type}
,semester = #{semester}
,time = #{time}
,title = #{title}
,credit = #{credit}
,week_plan = #{weekPlan}
,text_book = #{textBook}
,goal = #{goal}
,max_std = #{maxStd}
,grade = #{grade}
WHERE course_id = #{courseId}
